import React, { Component } from 'react'; //rcc
import axios from "axios";

function getDisplayName(WrappedComponent){
    return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

export function withApiProgress(WrappedComponent, apiPath) {
    return class extends Component {

        // static displayName = 'ApiProgress(' + getDisplayName(WrappedComponent) + ')';
        static displayName = `ApiProgress(${getDisplayName(WrappedComponent)})`;
        
        state = {
            pendingApiCall: false
        }

        componentDidMount() {
            this.requestInterceptor = axios.interceptors.request.use((request) => {
                // if (request.url === this.props.path) {
                //     this.setState({ pendingApiCall: true });
                // }
                console('running request intercepter', apiPath);
                this.updateApiCallFor(request.url, true);
                return request;
            });

            this.responseInterceptor = axios.interceptors.response.use((response) => {
                this.updateApiCallFor(response.url, false);
                return response;
            }, (error) => {
                this.updateApiCallFor(error.url, false);
                throw error;
            });
        }

        componentWillUnmount(){
            axios.interceptors.request.eject(this.requestInterceptor);
            axios.interceptors.response.eject(this.responseInterceptor);
        }

        updateApiCallFor = (url, inProgress) => {
            if (url === apiPath) {
                this.setState({ pendingApiCall: inProgress });
            }
        }

        render() {
            const { pendingApiCall } = this.state;
            // return (<div>{React.cloneElement(this.props.children, {pendingApiCall: pendingApiCall})}</div>);
            return <WrappedComponent pendingApiCall={pendingApiCall} {...this.props}/>
        }
    }
}
