import React, { Component } from "react";
import Input from "../components/Input";
import { withTranslation } from "react-i18next";
import { login } from "../api/apiCalls";
import ButtonWithProgress from "../components/ButtonWithProgress";

class LoginPage extends Component {
  state = {
    username: null,
    password: null,
    error: null,
    pendingApiCall: false
  };

  onChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
      error: null
    });
  };

  // onClickLogin = (event) => {
  //   event.preventDefault();
  //   const creds = {
  //     username: this.state.username,
  //     password: this.state.password,
  //   };
  //   login(creds);
  // };

  onClickLogin = async event => {
    event.preventDefault();
    const creds = {
      username: this.state.username,
      password: this.state.password,
    };
    this.setState({
      error: null
    });
    try {
      await login(creds);
    } catch (apiError) {
      this.setState({
        error: apiError.response.data.message
      });
    }
  };

  render() {
    const { t } = this.props;
    const { username, password, error, pendingApiCall } = this.state;
    const buttonEnabled = username && password;
    return (
      <div className="container">
        <form>
          <h1 className="text-center">{t("Login")}</h1>
          <Input label={t("Username")} name="username" onChange={this.onChange} ></Input>
          <Input label={t("Password")} name="password" type="password" onChange={this.onChange} ></Input>
          {error && <div className="alert alert-danger"> {error} </div>}
          <div className="text-center">
            <ButtonWithProgress 
              onClick={this.onClickLogin}
              disabled={!buttonEnabled || pendingApiCall}
              text={t('Login')}/>
            {/* <button className="btn btn-primary" onClick={this.onClickLogin} disabled={!buttonEnabled || pendingApiCall}>{t("Login")}</button> */}
          </div>
        </form>
      </div>
    );
  }
}

export default withTranslation()(LoginPage);
