import React from 'react';
import { signup } from '../api/apiCalls';

class UserSignupPage extends React.Component {

    state = {
        username: null,
        agreedClicked: false,
        displayName: null,
        password: null,
        passwordRepeat: null,
        pendingApiCall: false,
        errors: {}
    };

    onChange = event => {

        const { name, value } = event.target; // object destruction event.target'taki value ve name property'lerini eşleştiği name value değişkenlerine atar.

        // const value = event.target.value;
        // const name = event.target.name;
        this.setState({
            [name]: value
        });
    };

    onChangeAgree = event => {
        this.setState({
            agreedClicked: event.target.checked
        });
    };

    onClickSignUp = async event => {
        event.preventDefault();

        const { username, displayName, password } = this.state;

        const body = {
            username,
            displayName,
            password
        };

        // const body = {
        //     username: username,
        //     displayName: displayName,
        //     password: password
        // };

        this.setState({ pendingApiCall: true });
        try {
            const response = await signup(body); // sync
        } catch (error) {
            this.setState({ errors: error.response.data.validationErrors });
        }

        this.setState({ pendingApiCall: false });

        // signup(body) //async
        //     .then((response) => { //success
        //         this.setState({ pendingApiCall: false });
        //     }).catch(error => { //error
        //         this.setState({ pendingApiCall: false });
        //     });
    };

    // onChangeUsername = (event) => {
    //     this.setState({
    //         username: event.target.value
    //     });
    // };

    // onChangeDisplayName = (event) => {
    //     this.setState({
    //         displayName: event.target.value
    //     });
    // };

    // onChangePassword = (event) => {
    //     this.setState({
    //         password: event.target.value
    //     });
    // };

    // onChangePasswordRepeat = (event) => {
    //     this.setState({
    //         passwordRepeat: event.target.value
    //     });
    // };


    render() {
        const { pendingApiCall, errors } = this.state;
        const { username } = errors;

        return (
            <div className="container">
                <form>
                    <h1 className='text-center'>Sign Up</h1>
                    <div className='mb-3'>
                        <label>Username</label>
                        <input className={username ? "form-control is-invalid" : "form-control"} name="username" onChange={this.onChange} />
                        <div className="invalid-feedback">{errors.username}</div>
                    </div>
                    <div className='mb-3'>
                        <label>Display Name</label>
                        <input className="form-control" name="displayName" onChange={this.onChange} />
                    </div>
                    <div className='mb-3'>
                        <label>Password</label>
                        <input className="form-control" name="password" type="password" onChange={this.onChange} />
                    </div>
                    <div className='mb-3'>
                        <label>Password Repeat</label>
                        <input className="form-control" name="passwordRepeat" type="password" onChange={this.onChange} />
                    </div>
                    <div>
                        <input type="checkbox" onChange={this.onChangeAgree} /> Agreed
                    </div>
                    <div className='text-center'>
                        <button className='btn btn-primary' onClick={this.onClickSignUp} disabled={pendingApiCall}>
                            {pendingApiCall && <span className='spinner-border spinner-border-sm'></span>}
                            Sign Up
                        </button>
                    </div>
                </form>
            </div>
        );
    }
}

export default UserSignupPage;
