import React, { useContext } from 'react';
import logo from '../assets/hoaxify.png';
import { Link } from 'react-router-dom';
import { withTranslation } from 'react-i18next';
// import { connect } from 'react-redux';
// import { logoutSuccess } from '../redux/authActions';
import { Authentication } from '../shared/AuthenticationContext';

const TopBar = (props) => {

    // static contextType = Authentication;

    const context = useContext(Authentication);

    // const { t, username, isLoggedIn, onLogoutSuccess } = props;
    const { t } = props;
    const { onLogoutSuccess, state } = context;
    const { username, isLoggedIn } = state;

    let links = (
        <ul className='navbar-nav ml-auto'>
            <li>
                <Link className='nav-link' to="/login">
                    {t('Login')}
                </Link>
            </li>
            <li>
                <Link className='nav-link' to="/signup">
                    {t('Sign Up')}
                </Link>
            </li>
        </ul>
    );
    if (isLoggedIn) {
        links = (
            <ul className='navbar-nav ml-auto'>
                <li>
                    <Link className='nav-link' to={`/user/${username}`}>
                        {username}
                    </Link>
                </li>
                <li className='nav-link' onClick={onLogoutSuccess} style={{ cursor: 'pointer' }}>{t('Logout')}</li>
            </ul>
        );
    }
    return (
        <div className='shadow-sm bg-light mb-2'>
            <nav className="navbar navbar-light container navbar-expand">
                <div className="container-fluid">
                    <Link className="navbar-brand" to="/">
                        <img src={logo} width="55" alt='Hoaxify Logo' />Hoaxify
                    </Link>
                    {links}
                </div>
            </nav>
        </div>
    );
}

const TopBarWithTranslation = withTranslation()(TopBar)

// const mapStateToProps = (store) =>  {
//     return {
//         isLoggedIn: store.isLoggedIn,
//         username: store.username
//     }
// }

// const mapDispatchToProps = dispatch => {
//     return{
//         onLogoutSuccess: () => dispatch(logoutSuccess())
//     };
// }

// export default connect(mapStateToProps, mapDispatchToProps)(TopBarWithTranslation);

export default TopBarWithTranslation;
