import Input from "../components/Input";
import { withTranslation } from "react-i18next";
import ButtonWithProgress from "../components/ButtonWithProgress";
import { withApiProgress } from "../shared/ApiProgress";
import { connect } from 'react-redux';
import { loginHandler } from "../redux/authActions";
import { useState } from "react";
// import { Authentication } from "../shared/AuthenticationContext";

const LoginPage = (props) => {
  // static contextType = Authentication;

  const [username, setUserName] = useState();

  // state = {
  //   username: null,
  //   password: null,
  //   error: null,
  //   pendingApiCall: false
  // };

  // const onChange = (event) => {
  //   const { name, value } = event.target;
    // this.setState({
    //   [name]: value,
    //   error: null
    // });
  // };

  // onClickLogin = (event) => {
  //   event.preventDefault();
  //   const creds = {
  //     username: this.state.username,
  //     password: this.state.password,
  //   };
  //   login(creds);
  // };

  const onClickLogin = async event => {
    event.preventDefault();
    // const { username, password } = this.state;
    const creds = {
      username,
      password
    };

    const { history, dispatch } = props;
    const { push } = history;

    this.setState({
      error: null
    });
    try {
      await dispatch(loginHandler(creds));
      push('/');
    } catch (apiError) {
      console.log(apiError)
      // this.setState({
      //   error: apiError.response.data.message
      // });
    }
  };
  const { t, pendingApiCall } = props;
  let password, error = undefined;

  const buttonEnabled = username && password;
  return (
    <div className="container">
      <form>
        <h1 className="text-center">{t("Login")}</h1>
        <Input label={t("Username")} name="username" onChange={(event) => {setUserName(event.target.value)}}/>
        <Input label={t("Password")} name="password" type="password" onChange={onChange} ></Input>
        {error && <div className="alert alert-danger"> {error} </div>}
        <div className="text-center">
          <ButtonWithProgress
            onClick={onClickLogin}
            disabled={!buttonEnabled || pendingApiCall}
            pendingApiCall={pendingApiCall}
            text={t('Login')} />
          {/* <button className="btn btn-primary" onClick={this.onClickLogin} disabled={!buttonEnabled || pendingApiCall}>{t("Login")}</button> */}
        </div>
      </form>
    </div>
  );
}

const LoginPageWithTranslation = withTranslation()(LoginPage);

export default connect()(withApiProgress(LoginPageWithTranslation));
