import React, { Component } from 'react';
import logo from '../assets/hoaxify.png';
import { Link } from 'react-router-dom';
import { withTranslation } from 'react-i18next';
import { connect } from 'react-redux';
// import { Authentication } from '../shared/AuthenticationContext';

class TopBar extends Component {

    // static contextType = Authentication;

    onClickLogout = () => {
        const action = {
            type: 'logout-success'
        }
        this.props.dispatch(action);
    }

    render() {
        const { t, username, isLoggedIn } = this.props;

        let links = (
            <ul className='navbar-nav ml-auto'>
                <li>
                    <Link className='nav-link' to="/login">
                        {t('Login')}
                    </Link>
                </li>
                <li>
                    <Link className='nav-link' to="/signup">
                        {t('Sign Up')}
                    </Link>
                </li>
            </ul>
        );
        if (isLoggedIn) {
            links = (
                <ul className='navbar-nav ml-auto'>
                    <li>
                        <Link className='nav-link' to={`/user/${username}`}>
                            {username}
                        </Link>
                    </li>
                    <li className='nav-link' onClick={this.onClickLogout} style={{ cursor: 'pointer' }}>{t('Logout')}</li>
                </ul>
            );
        }
        return (
            <div className='shadow-sm bg-light mb-2'>
                <nav className="navbar navbar-light container navbar-expand">
                    <div className="container-fluid">
                        <Link className="navbar-brand" to="/">
                            <img src={logo} width="55" alt='Hoaxify Logo' />Hoaxify
                        </Link>
                        {links}
                    </div>
                </nav>
            </div>
        );
    }
}

const TopBarWithTranslation = withTranslation()(TopBar)

const mapStateToProps = (store) =>  {
    return {
        store
    }
}

export default connect(mapStateToProps)(TopBarWithTranslation);
