import React from 'react';
import { signup, changeLanguage } from '../api/apiCalls';
import Input from '../components/Input';
import { withTranslation } from 'react-i18next';

class UserSignupPage extends React.Component {

    state = {
        username: null,
        agreedClicked: false,
        displayName: null,
        password: null,
        passwordRepeat: null,
        pendingApiCall: false,
        errors: {}
    };

    onChange = event => {
        const { t } = this.props;
        const { name, value } = event.target; // object destruction event.target'taki value ve name property'lerini eşleştiği name value değişkenlerine atar.

        // const value = event.target.value;
        // const name = event.target.name;

        const errors = { ...this.state.errors };
        errors[name] = undefined;
        if (name === 'password' || name === 'passwordRepeat') {
            if (name == 'password' && value !== this.state.passwordRepeat) {
                errors.passwordRepeat = t('Password mismatch');
            } else if (name === 'passwordRepeat' && value !== this.state.password) {
                errors.passwordRepeat = t('Password mismatch');
            } else {
                errors.passwordRepeat = undefined;
            }
        }
        this.setState({
            [name]: value,
            errors
        });
    };

    onChangeAgree = event => {
        this.setState({
            agreedClicked: event.target.checked
        });
    };

    onClickSignUp = async event => {
        event.preventDefault();

        const { username, displayName, password } = this.state;

        const body = {
            username,
            displayName,
            password
        };

        // const body = {
        //     username: username,
        //     displayName: displayName,
        //     password: password
        // };

        this.setState({ pendingApiCall: true });
        try {
            const response = await signup(body); // sync
        } catch (error) {
            if (error.response.data.validationErrors) {
                this.setState({ errors: error.response.data.validationErrors });
            }
        }

        this.setState({ pendingApiCall: false });

        // signup(body) //async
        //     .then((response) => { //success
        //         this.setState({ pendingApiCall: false });
        //     }).catch(error => { //error
        //         this.setState({ pendingApiCall: false });
        //     });
    };

    onChangeLanguage = language => {
        const { i18n } = this.props;
        i18n.changeLanguage(language);
        changeLanguage(language);
    };

    // onChangeUsername = (event) => {
    //     this.setState({
    //         username: event.target.value
    //     });
    // };

    // onChangeDisplayName = (event) => {
    //     this.setState({
    //         displayName: event.target.value
    //     });
    // };

    // onChangePassword = (event) => {
    //     this.setState({
    //         password: event.target.value
    //     });
    // };

    // onChangePasswordRepeat = (event) => {
    //     this.setState({
    //         passwordRepeat: event.target.value
    //     });
    // };


    render() {
        const { t } = this.props
        const { pendingApiCall, errors } = this.state;
        const { username, displayName, password, passwordRepeat } = errors;

        return (
            <div className="container">
                <form>
                    <h1 className='text-center'>{t('Sign Up')}</h1>
                    <Input name="username" label={t('Username')} error={username} onChange={this.onChange}></Input>
                    <Input name="displayName" label={t('Display Name')} error={displayName} onChange={this.onChange}></Input>
                    <Input name="password" label={t("Password")} error={password} onChange={this.onChange} type="password"></Input>
                    <Input name="passwordRepeat" label={t("Password Repeat")} error={passwordRepeat} onChange={this.onChange} type="password"></Input>
                    {/* <div className='mb-3'>
                        <label>Password Repeat</label>
                        <input className="form-control" name="passwordRepeat" type="password" onChange={this.onChange} />
                    </div> */}
                    <div>
                        <input type="checkbox" onChange={this.onChangeAgree} /> {t("Agree")}
                    </div>
                    <div className='text-center'>
                        <button className='btn btn-primary' onClick={this.onClickSignUp} disabled={pendingApiCall || passwordRepeat != undefined}>
                            {pendingApiCall && <span className='spinner-border spinner-border-sm'></span>}
                            {t('Sign Up')}
                        </button>
                    </div>
                    <div>
                        <img src="https://flagsapi.com/TR/shiny/64.png" alt="Turkish Flag" onClick={()=> this.onChangeLanguage('tr')} style={{cursor: 'pointer'}}/>
                        <img src="https://flagsapi.com/US/shiny/64.png" alt="USA Flag" onClick={()=> this.onChangeLanguage('en')} style={{cursor: 'pointer'}}/>
                    </div>
                </form>
            </div>
        );
    }
}

const UserSignupPageWithTranslation = withTranslation()(UserSignupPage);

export default UserSignupPageWithTranslation;
