import Input from "../components/Input";
import { useTranslation } from "react-i18next";
import ButtonWithProgress from "../components/ButtonWithProgress";
import { useApiProgress } from "../shared/ApiProgress";
import { useDispatch } from 'react-redux';
import { loginHandler } from "../redux/authActions";
import { useEffect, useState } from "react";

const LoginPage = (props) => {

  const [username, setUsername] = useState();
  const [password, setPassword] = useState();
  const [error, setError] = useState();
  const dispatch = useDispatch();

  useEffect(() => {
    setError(undefined);
  }, [username, password]); // bu değişkenlerin değişikliği sırasında tetiklenir

  const onClickLogin = async event => {
    event.preventDefault();
    const creds = {
      username,
      password
    };

    const { history } = props;
    const { push } = history;

    setError(undefined);
    try {
      await dispatch(loginHandler(creds));
      push('/');
    } catch (apiError) {
      console.log(apiError);
      setError(apiError.response.data.message);
    }
  };
  const { t } = useTranslation();

  const pendingApiCall = useApiProgress('/api/1.0/auth');
  
  const buttonEnabled = username && password;
  return (
    <div className="container">
      <form>
        <h1 className="text-center">{t("Login")}</h1>
        <Input label={t("Username")} onChange={(event) => { setUsername(event.target.value); }} />
        <Input label={t("Password")} type="password" onChange={(event) => { setPassword(event.target.value) }} />
        {error && <div className="alert alert-danger"> {error} </div>}
        <div className="text-center">
          <ButtonWithProgress
            onClick={onClickLogin}
            disabled={!buttonEnabled || pendingApiCall}
            pendingApiCall={pendingApiCall}
            text={t('Login')} />
          {/* <button className="btn btn-primary" onClick={this.onClickLogin} disabled={!buttonEnabled || pendingApiCall}>{t("Login")}</button> */}
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
